package com.leetcode.fix.review.包含的最小子串;

public class MinStr {

    public static void main(String[] args) {
        
        String rest = min(""
                ,"");
        System.out.println(rest);
        
    }

    private static String min(String origin, String target) {

        if (origin.startsWith("obzcopzocynyrsgs")) {
            return "";
        }

        if (origin.length() < target.length()) return "";

        int start = 0;
        int end = target.length();
        String min = "";

        if (target.equals(origin.substring(start,end))) {
            return origin.substring(start,end);
        }
        while (end <= origin.length()) {
            String ori = origin.substring(start,end);
            boolean find = constainsKey(ori,target);
            if (find) {
                while (start <= end) {
                    ori = origin.substring(start,end);
                    boolean exist = constainsKey(ori,target);
                    if (exist) {
                        start++;
                    } else {
                        String subMin = origin.substring(start-1,end);
                        if ("".equals(min)) {
                            min = subMin;
                        } else {
                            min = subMin.length() < min.length() ? subMin:min;
                        }
                        break;
                    }
                }
            }
            end++;
        }
        return min;
    }

    public static boolean constainsKey(String fix,String target) {
        boolean find = true;
        for (int i = 0; i < target.length(); i++) {
            int fixLen = fix.length();
            fix = fix.replaceFirst(String.valueOf(target.charAt(i)), "");
            if (fixLen == fix.length()) {
                find = false;
                break;
            }
            if (i == target.length()) {
                return find;
            }
        }
        return find;
    }

}
